from flask import Flask
app = Flask(__name__)
@app.route('/')
def home():
  return 'Hello, Flask!'
app.run(debug=True)

app.route('/greet/<name>')
def greet(name):
  return f'Hello, {name}!'

from flask import render_template
@app.route('/')
def home():
  return render_template('index.html')

